mapper.select:
SELECT decode(to_char(sysdate,'yy.mm.dd'), to_char(x.logtime,'yy.mm.dd'), to_char(x.logtime,'hh24:mi:ss'), to_char(x.logtime,'yy.mm.dd')) logtime , x.* FROM(
SELECT rownum rang, z.* FROM( SELECT b.seq, b.name, b.id, b.email, b.subject, b.content, b.hit, b.logtime, b.bcode, r.rseq, r.ref, r.lev, r.step, r.pseq, r.reply FROM board b, reboard r WHERE b.seq = r.seq AND b.bcode = #{bcode}
AND b.subject like '%'||#{word}||'%'
AND b.${key} = #{word}
ORDER BY logtime DESC)z WHERE rownum <= #{end})x WHERE x.rang > #{start}
